flow.func:
const STATE_UNKNOWN = 0
const STATE_IDLE = 1
const STATE_COMPRESSOR_DELAY = 2
const STATE_COOLING = 3
const STATE_HEATING = 4

const TARGET = "TARGET_TEMPERATURE"
const DIFFERENCE = "TEMPERATURE_DIFFERENCE"
const COMPRESSOR = "COMPRESSOR_DELAY_MINUTES"

let compressorMsg = setOverrides(msg)

let targetTemperature = readVariable(TARGET)
let differenceSetValue = readVariable(DIFFERENCE)
let compressorDelayTimeMinutes = readVariable(COMPRESSOR)
let previousState = flow.get("CURRENT_STATE") || STATE_UNKNOWN
let currentCompressorDelay = Math.max(0, ((flow.get("COMPRESSOR_STOPPED") || 0) + compressorDelayTimeMinutes*60000) - new Date().getTime())
let lastMeasurement = flow.get("LAST_MEASURED_TEMPERATURE")

let measuredTemperature = msg.payload
let hasNewMeasurement = typeof measuredTemperature == "number"

if (hasNewMeasurement) {
    lastMeasurement = measuredTemperature
} else if (lastMeasurement == null) {
    lastMeasurement = "?"
}

let coolingMsg = null
let heatingMsg = null
let statusMsg = null

if (msg.context != null) {
    flow.keys().forEach( it => 
        node.warn(it + " = " + flow.get(it))
    )
}

if (targetTemperature == -99999) {
    return [compressorMsg, null, null, error("Target temperature not set")]
}


flow.set("LAST_MEASURED_TEMPERATURE", lastMeasurement)

let newState = STATE_UNKNOWN
if (hasNewMeasurement) {
    if (measuredTemperature >= targetTemperature + differenceSetValue) {
        newState = (previousState == STATE_COOLING) ? STATE_COOLING : STATE_COMPRESSOR_DELAY
    } else if (measuredTemperature <= targetTemperature - differenceSetValue) {
        newState = STATE_HEATING
    } else {
        if ((previousState == STATE_COOLING || previousState == STATE_COMPRESSOR_DELAY) && measuredTemperature > targetTemperature) {
            newState = previousState
        } else if (previousState == STATE_HEATING && measuredTemperature < targetTemperature) {
            newState = previousState
        } else {
            newState = STATE_IDLE
        }
    }
}
if (newState == STATE_UNKNOWN && coolingMsg == null && msg.cooling != null && previousState != STATE_COMPRESSOR_DELAY && msg.cooling != isCooling(previousState)) {
    if (!msg.cooling) {
        // Cooling should be turned on
        newState = STATE_COMPRESSOR_DELAY
        // We don't have a reliable compressor delay time so we need to default to full delay
        currentCompressorDelay = compressorDelayTimeMinutes * 60000
    } else {
        // Cooling should be turned off
        coolingMsg = [{ reset: true}, { payload: false, delay: 0, topic: "cooling-controller" }]
    }
}

if (newState == STATE_UNKNOWN && heatingMsg == null && msg.heating != null && msg.heating != (previousState == STATE_HEATING)) {
    if (!msg.heating) {
        // Heating should be turned on
        newState = STATE_HEATING
        heatingMsg = { payload: true, topic: "heating-controller" }
    } else {
        // Heating should be turned off
        heatingMsg = { payload: false, topic: "heating-controller" }
    }    
}

if (newState != STATE_UNKNOWN && newState != previousState) {
    if (previousState == STATE_COOLING || previousState == STATE_COMPRESSOR_DELAY) {
        coolingMsg = [{ reset: true}, { payload: false, delay: 0, topic: "cooling-controller" }]
        if (previousState == STATE_COOLING) {
            flow.set("COMPRESSOR_STOPPED", new Date().getTime())
        }
    } else if (previousState == STATE_HEATING) {
        heatingMsg = { payload: false, topic: "heating-controller" }
    }
    if (newState == STATE_COMPRESSOR_DELAY) {
        coolingMsg = [{ reset: true}, { payload: true, delay: currentCompressorDelay, topic: "cooling-controller" }]
    } else if (newState == STATE_HEATING) {
        heatingMsg = { payload: true, topic: "heating-controller"}
    }
}

let now = new Date()
let compressorDelayDate = new Date(new Date().getTime() + currentCompressorDelay)
let reportedMeasurement = measuredTemperature != null ? measuredTemperature : lastMeasurement
let reportedState = newState != STATE_UNKNOWN ? newState : previousState
statusMsg = status(
    reportedMeasurement + "°C (" + targetTemperature + ")" + stateToText(reportedState, compressorDelayDate.getTime() > now.getTime() ? compressorDelayDate : now),
    stateToColor(reportedState),
    stateToShape(reportedState)
)

flow.set("CURRENT_STATE", reportedState)

return [
    compressorMsg,
    coolingMsg,
    heatingMsg,
    statusMsg
]

function isCooling(newState) {
    return newState == STATE_COOLING || newState == STATE_COMPRESSOR_DELAY
}

function error(text) {
    return status(text, "red", "dot")
}

function status(text, fill, shape) {
    return {
        payload: { text, fill: fill, shape: shape }
    }
}

function stateToText(state, compressorDelay) {
    switch (state) {
        case STATE_IDLE: return " (idle)"
        case STATE_COMPRESSOR_DELAY: {
            let delay = compressorDelay.toLocaleTimeString([], {
                    hour: '2-digit',
                    minute: '2-digit',
                    second: '2-digit',
                    hour12: false
            });
            return ` (compressor delay ${delay})`
        }
        case STATE_COOLING: return " (cooling)"
        case STATE_HEATING: return " (heating)"
        default: return ""
    }
}

function stateToColor(state) {
    switch (state) {
        case STATE_IDLE: return "grey"
        case STATE_COMPRESSOR_DELAY: case STATE_COOLING: return "blue"
        case STATE_HEATING: return "green"
        default: return "grey"
    }
}

function stateToShape(state) {
    switch (state) {
        case STATE_COMPRESSOR_DELAY: return "ring"
        default: return "dot"
    }
}

function setOverrides(msg) {
    let compressorMsg = null
    if (msg.target != null) {
        if (typeof msg.target == "number") {
            flow.set(TARGET, msg.target)
        } else {
            node.warn("msg.target is not a number: " + msg.target)
        }     
    }

    if (msg.difference != null) {
        if (typeof msg.difference == "number") {
            flow.set(DIFFERENCE, msg.difference)
        } else {
            node.warn("msg.difference is not a number: " + msg.difference)
        }
    }

    if (msg.compressor != null) {
        if (typeof msg.compressor == "number") {
            let previousCompressorDelay = readVariable(COMPRESSOR)
            flow.set(COMPRESSOR, msg.compressor)
            if (previousCompressorDelay != msg.compressor) {
                compressorMsg = { flush: true }
            }
        } else {
            node.warn("msg.compressor is not a number: " + msg.compressor)
        }
    }
    return compressorMsg
}

function readVariable(name) {
    let value = flow.get(name)
    if (value == null) {
        return env.get(name)
    }
    return value
}

function stateToStr(state) {
    switch (state) {
        case STATE_UNKNOWN: return "UNKNOWN"
        case STATE_COMPRESSOR_DELAY: return "COMPRESSOR_DELAY"
        case STATE_COOLING: return "COOLING"
        case STATE_HEATING: return "HEATING"
        case STATE_IDLE: return "IDLE"
        default: return "<???>"
    }
}

flow.initialize:
// Code added here will be run once
// whenever the node is started.

if (flow.get("CURRENT_STATE") == 2) {
    flow.set("CURRENT_STATE", 0)
}

flow.func:
if (msg.payload == true) {
    flow.set("CURRENT_STATE", 3)
    let lastMeasurement = flow.get("LAST_MEASURED_TEMPERATURE")
    let targetTemperature = flow.get("TARGET_TEMPERATURE")
    return {
        payload: {
            text: lastMeasurement + "°C (" + targetTemperature + ") (cooling)",
            fill: "blue",
            shape: "dot"
        }
    }
}

if (msg.payload != null) {
    delete msg.delay
    return msg
}
